class Solution {
    fun findLength(nums1: IntArray, nums2: IntArray): Int {
        var p = 0
        var maxSize = 0
        var currentMaxSize = 0
        var j = 0
        var savedJ = 0

        //   *
        //[1,2,3,2,1]
        // *
        //[3,2,1,4,7]
        //i = 1
        //currentMaxSize = 0
        //p = 1
        //maxSize = 1
        //j = 4
        //savedJ = 0

        for (i in 0 until nums1.size) {
            p = i
            currentMaxSize = 0
            savedJ = 0
            j = 0

            loop@ while(j < nums2.size){

                if (p < nums1.size){
                    if (nums1[p] != nums2[j]){
                        maxSize = maxOf(maxSize, currentMaxSize)
                        p = i
                        currentMaxSize = 0
                        j = savedJ + 1
                        savedJ = j
                        continue@loop
                    }
                }else{
                    break@loop
                }

                currentMaxSize++
                p++
                j++
            }

            maxSize = maxOf(maxSize, currentMaxSize)
        }


        return maxSize
    }
}